import { Outlet } from "react-router-dom";
import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';

import '../../styles/layout.css';




import icon_home from '../../assets/pictures/icons/home.png';



export default function Layout() {
  const navigate = useNavigate();
  const [style, setStyle] = useState("rollState");
  let change =0;
  const roll = () => {
    if(change===0)
    {
      change=1;
      console.log("you just clicked");
      setStyle("rollState2");
    }
    else
    {
      setStyle("rollState");
      change=0;
    }

  };


  return (
      <>
        <aside className={style} id='sidebar'>
          <div onClick={() => roll()} id='logo'>Logo</div>
          <div id='menu'>
            <button onClick={() => navigate('home')}><img class='menu-icon'alt='home' src={icon_home}></img><span>Home</span></button>
            <button onClick={() => navigate('shop')}>Shop</button>
            <button onClick={() => navigate('pages')}>Pages</button>
            <button onClick={() => navigate('apperance')}>Apperance</button>
            <button onClick={() => navigate('settings')}>Settings</button>
          </div>
          <button id='logout'onClick={() => navigate('../')}>Logout</button>
        </aside>
        <section id='main'>
            <Outlet/>
        </section>
      </>
  );
}
